import React from "react";


const Todos = props => {
handleDoubleClick = (e) =>{
        console.log("abc");
}
  const { todos, deleteTodo, checkTodo,} = props;
  const TodoList = todos.length ? (
    todos.map((todo, i) => {
      return (
        <div className="todolist" key={i}>
          <span>
              {todo.iscompleted ? <input
                className="status"
                type="checkbox"
                checked
                onChange={() => {
                    checkTodo(i);
                }}
                /> : <input
                className="status"
                type="checkbox"
                onChange={() => {
                    checkTodo(i);
                }}
                />}

          </span>
          {todo.iscompleted ? <span className="listitem strike">{todo.todocaption}</span> : <span className="listitem" onDoubleClick={this.handleDoubleClick}>{todo.todocaption}</span>}
          <span
            className="cross"
            onClick={() => {
              deleteTodo(i);
            }}
          >
            X
          </span>
        </div>
      );
    })
  ) : (
    <p className="listitem">No Left Todo </p>
  );
  return <div>{TodoList}</div>;
};

export default Todos;
