import React from 'react';

const Todos = (props) =>{
const {todos,deleteTodo,checkTodo } = props;
const TodoList = todos.length ? ( 
    todos.map((todo, i) => {
        return (
            <div className="todolist" key={i}> 
                <span><input className="status" type="checkbox" onClick={()=>{checkTodo(i)}}/></span>
                <span className="listitem">{todo.todocaption}</span>
                <span className="cross" onClick={()=>{deleteTodo(i)}}>X</span>
            </div>
        )
    })
) : ( <p className="listitem">No Left Todo </p>)
return (
    <div>
        {TodoList}
    </div>
)
}

export default Todos
