import React from "react";


const Todos = (props) => {

  const { todos, deleteTodo, checkTodo,editTodo } = props;
  const TodoList = todos.length ? (
    todos.map((todo, i) => {
      return (
        <div className="todolist" key={i}>
          <span>
              {todo.iscompleted ? <input className="status" type="checkbox" checked onChange={checkTodo.bind(this,i)}/>
               :
               <input className="status" type="checkbox" onClick={checkTodo.bind(this,i)}/>}
          </span>
          {todo.iscompleted ?( <span className="listitem strike" >{todo.todocaption}</span>) : (<span className="listitem" onChange={editTodo.bind(this,i)}>{todo.todocaption}</span> )}
          <span><input type="text" value={todo.todocaption}/></span>
          <span className="cross" onClick={() => {deleteTodo(i)}}>X</span>
        </div>
      );
    })
  ) : (
    <p className="listitem">No Left Todo </p>
  );
  return <div>{TodoList}</div>;
};

export default Todos;
