import React, { Component } from 'react';
import Todos from './Todos';
import AddTodos from './Addtodos';

class App extends Component {
  state = {
    todos : [
      {
        id:1 , 
        todocaption: "Learn react" , 
        iscompleted:true
      },
      {
        id:2 , 
        todocaption: "Build Todo App" , 
        iscompleted:false
      }
    ]
  }
  checkTodo = (id) =>{
    let newtodos = [...this.state.todos];
    // var status = newtodos[id].iscompleted;
    // newtodos[id].iscompleted = !status;
    // this.setState({
    //   todos:newtodos
    // })
    console.log(id)
    // console.log(this.state.todos);
  }
  deleteTodo = (id) =>{
    let newtodos = this.state.todos.filter(todo => {
      return todo.id !==id 
    })
    this.setState({
      todos:newtodos
    })
  }
  addNewTodo = (newTodo) =>{
    newTodo.id = Math.random();
    let newtodos = [...this.state.todos,newTodo];
    this.setState({
      todos:newtodos
    })
  }
  render() {
    console.log(this.state.todos);
    return (
      <div className="App">
        <h1 className="mainhead"> Todos</h1>
        <AddTodos addNewTodo={this.addNewTodo}/>
        <Todos todos={this.state.todos} deleteTodo={this.deleteTodo} checkTodo={this.checkTodo}/>

      </div>
    );
  }
}

export default App;
